    data: {
        entity_id: "fan.master_bedroom_fan_level", 
        speed:"medium"
    }
}

return msg;

var lightLevel = flow.get("light_level_pct");

// set special level if provided
// caller is responsible for resetting levels
if(msg.special_level) {
    flow.set("light_level_custom_" + msg.topic, msg.special_level)
} else {    
    flow.set("light_level_custom_" + msg.topic, undefined)
}

